import { Component } from "react";
import { Link } from "react-router-dom";
import HelloWorldService from "../api/GymProgress/HelloWorldService";

class WelcomeComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            welcomeMessage: "",
            welcomeMessage
        };

        this.retrieveWelcomeMessage = this.retrieveWelcomeMessage.bind(this);
        this.handleSuccessfulResponse = this.handleSuccessfulResponse.bind(this);
        this.handleErrorResponse = this.handleErrorResponse.bind(this);
    }
    render() {
        return (
            <>
                <div className="container">
                    <h1>Welcome!</h1>
                    Welcome {this.props.params.name}! You can manage your progress
                    <Link to="/progress"> here</Link>.
                </div>
                <div className="container">
                    Click here to get a customized welcome message.
                    <button className="btn btn-success" onClick={this.retrieveWelcomeMessage}>
                        Get Welcome Message
                    </button>
                </div>
                <div className="container">{this.state.welcomeMessage}</div>
            </>
        );
    }

    retrieveWelcomeMessage() {
        HelloWorldService.executeHelloWorldPathVariableService(this.props.params.name)
            .then((response) => this.handleSuccessfulResponse(response))
            .catch((error) => this.handleErrorResponse(error));
    }

    handleSuccessfulResponse(response) {
        console.log(response);
        this.setState({
            welcomeMessage: response.data.message,
        });
    }

    handleErrorResponse(error) {
        console.log(error.response);
        this.setState({
            welcomeMessage: error.response.data.message,
        });
    }
}

export default WelcomeComponent;
