import React, { Component } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import withNavigation from "./WithNavigation";

class GymProgressApp extends Component {
    render() {
        const LoginComponent = withNavigation(LoginComponent);
        return (
            <div className="GymProgressApp">
                <BrowserRouter>
                    <Routes>
                        <Route
                            path="/"
                            element={<LoginComponentWithNavigation />}
                        />
                        <Route
                            path="/login"
                            element={<LoginComponentWithNavigation />}
                        />
                        <Route path="/welcome" element={<WelcomeComponent />} />
                    </Routes>
                </BrowserRouter>
            </div>
        );
    }
}

class LoginComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            username: "Cristi",
            password: "",
            loginFailed: false,
            loginSucceeded: false,
        };

        this.handleChange = this.handleChange.bind(this);
        this.loginClicked = this.loginClicked.bind(this);
    }

    handleChange(event) {
        this.setState({ [event.target.name]: event.target.value });
    }

    loginClicked() {
        if (
            this.state.username === "Cristi" &&
            this.state.password === "dummy"
        ) {
            this.props.navigate(`/welcome`);

            this.setState({ loginSucceeded: true });
            this.setState({ loginFailed: false });
        } else {
            this.setState({ loginSucceeded: false });
            this.setState({ loginFailed: true });
        }
    }

    render() {
        return (
            <div>
                {this.state.loginFailed && <div>Invalid Credentials</div>}
                {this.state.loginSucceeded && <div>Login Successful</div>}

                <label htmlFor="username">Username: </label>
                <input
                    type="text"
                    name="username"
                    id="username"
                    value={this.state.username}
                    onChange={this.handleChange}
                />

                <label htmlFor="password">Password: </label>
                <input
                    type="password"
                    name="password"
                    id="password"
                    value={this.state.password}
                    onChange={this.handleChange}
                />

                <button onClick={this.loginClicked}>Login</button>
            </div>
        );
    }
}

class WelcomeComponent extends Component {
    render() {
        return <div>Welcome to Gym Progress Tracker App!</div>;
    }
}

export default GymProgressApp;
