            ProgressDataService.createTodo(username, todo).then(() => this.props.navigate("/progresses"));
        } else {
            ProgressDataService.updateTodo(username, this.state.id, todo).then(() =>
                this.props.navigate("/progresses")
            );
        }
    }

    render() {
        let { description, date } = this.state;

        return (
            <div>
                <h1>Todo</h1>
                <div className="container">
                    <Formik
                        initialValues={{ description, date }}
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        validate={this.validate}
                        enableReinitialize={true}
                    >
                        {(props) => (
                            <Form>
                                <ErrorMessage name="description" component="div" className="alert alert-warning" />
                                <ErrorMessage name="date" component="div" className="alert alert-warning" />
                                <fieldset className="form-group">
                                    <label>Description</label>
                                    <Field className="form-control" type="text" name="description" />
                                </fieldset>
                                <fieldset className="form-group">
                                    <label>Date</label>
                                    <Field className="form-control" type="date" name="date" />
                                </fieldset>
                                <button className="btn btn-success" type="submit">
                                    Save
                                </button>
                            </Form>
                        )}
                    </Formik>
                </div>
            </div>
        );
    }
}

export default ProgressComponent;
