import { Component } from "react";
import ProgressDataService from "../api/GymProgress/ProgressDataService";
import AuthenticationService from "./AuthenticationService";

class ProgressComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            day: this.props.params.day,
        };
    }

    componentDidMount() {
        if (this.state.day === -1) {
            return;
        }

        let username = AuthenticationService.getLoggedInUsername();

        ProgressDataService.retrieveTodo(username, this.state.id).then((response) =>
            this.setState({
                description: response.data.description,
                targetDate: moment(response.data.targetDate).format("YYYY-MM-DD"),
            })
        );
    }

    onSubmit(values) {
        let username = AuthenticationService.getLoggedInUserName();

        let todo = {
            id: this.state.id,
            description: values.description,
            targetDate: values.targetDate,
        };

        if (this.state.id === -1) {
            ProgressDataService.createProgress(username, todo).then(() => this.props.navigate("/todos")); //REACT-6
        } else {
            ProgressDataService.updateProgress(username, this.state.id, todo).then(() => this.props.navigate("/todos")); //REACT-6
        }

        console.log(values);
    }

    render() {
        return <div>Progress Component for Day - {this.props.match.params.day}</div>;
    }
}

export default ProgressComponent;
