import { Component } from "react";
import ProgressDataService from "../api/GymProgress/ProgressDataService";
import AuthenticationService from "./AuthenticationService";

class ListProgressesComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            progresses: [],
        };
    }

    componentDidMount() {
        let username = AuthenticationService.getLoggedInUsername();
        ProgressDataService.retrieveAllProgresses(username).then
    }

    render() {
        return (
            <div>
                <h1>Progresses</h1>
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Day</th>
                                <th>Description</th>
                                <th>Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.progresses.map((progress) => (
                                <tr key={progress.day}>
                                    <td>{progress.day}</td>
                                    <td>{progress.description}</td>
                                    <td>{progress.date.toString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default ListProgressesComponent;
